@Component({
  selector: 'app-penthouse',
  templateUrl: './penthouse.component.html',
  styleUrls: ['./penthouse.component.css']
})
export class PenthouseComponent {
  address:string='Kolkata';
  name:string='Disney';
}
<p>I'm the PentHouse Component {{ address }} .</p>
<p>My name is {{name}} .</p>
